        function beforeLeave() {
            return document.getElementById('main').innerHTML;
        }

        let logoRef = document.querySelector('.logo-ref');
        let navItem = document.querySelectorAll('.nav__link');

        function navItemBinding(elem) {
            let currentPage = beforeLeave();
            elem.addEventListener('click', function () {

                let page = this.getAttribute('data-page');

                xhr.open('GET', `../${page}.html`);
                xhr.send();
                xhr.onreadystatechange = function () {
                    if (this.readyState === 4) {
                        document.getElementById('main').innerHTML = this.responseText;
                        if (page === 'home') {
                            homeInit();
                        }
                        if (page === 'about-us') {
                            aboutUsInit();
                        }
                        if (page === 'blog') {
                            blogInit();
                        }
                        if (page === 'contact-us') {
                            contactUsInit();
                        }

                        menuInit();
                        scrollTo(0, 0);
                    }
                };
            });
        }

        function navItemBindingMass(item) {
            for (let i = 0; i < item.length; i++) {
                navItemBinding(item[i]);
            }
        }

        navItemBinding(logoRef);
        navItemBindingMass(navItem);



