        /* --- accordion BEGIN --- */

        function accordionInit() {
            let accordionItem = document.querySelectorAll('.accordion__item');

            for (let i = 0; i < accordionItem.length; i++) {
                accordionItem[i].addEventListener('click', function (e) {
                    let items = this.parentElement.children;

                    for (let j = 0; j < items.length; j++) {
                        if (items[j] !== this) {
                            items[j].querySelector('.arrow-area').classList.remove('choosen-arrow');
                            items[j].querySelector('.accordion__title-area').classList.remove('choosen-title');
                            items[j].querySelector('.accordion__info').classList.add('hidden');
                        }
                    }

                    this.querySelector('.arrow-area').classList.toggle('choosen-arrow');
                    this.querySelector('.accordion__title-area').classList.toggle('choosen-title');
                    this.querySelector('.accordion__info').classList.toggle('hidden');
                });
            }
        }

