        /* --- menu BEGIN --- */

        function menuInit() {

            fadeInit();

            let menu = document.querySelector('.menu');
            let logo = document.querySelector('.logo');
            let logoInitialSrc = logo.getAttribute('src');
            let mobileNavItem = document.querySelectorAll('.nav__mobile .nav__item');

            if (document.documentElement.clientWidth < 576) {
                menu.classList.remove('hidden');
                logo.setAttribute('src', logo.getAttribute('data-src'));
            }

            window.addEventListener('resize', function () {

                if (document.documentElement.clientWidth < 576) {
                    menu.classList.remove('hidden');
                    logo.setAttribute('src', logo.getAttribute('data-src'));

                } else if (document.documentElement.clientWidth >= 576) {
                    menu.classList.add('hidden');
                    logo.setAttribute('src', logoInitialSrc);
                }

            });

            menu.addEventListener('click', function () {
                let fadedBg = document.querySelector('.faded-bg');
                fadedBg.classList.remove('hidden');

                let menuMobile = document.querySelector('.nav__mobile');
                menuMobile.classList.remove('hidden');
            });

            for (let i = 0; i < mobileNavItem.length; i++) {
                mobileNavItem[i].addEventListener('click', function () {

                    let fadedBg = document.querySelector('.faded-bg');
                    fadedBg.classList.add('hidden');

                    let menuMobile = document.querySelector('.nav__mobile');
                    menuMobile.classList.add('hidden');
                });
            }
        }


