        /* Находим нужный блог по data-id в массиве blogs */
        function searchBlogsItem(dataId) {
            for (let i = 0; i < blogs.length; i++) {
                if (blogs[i].id === dataId) {
                    return blogs[i];
                }
            }
            return false;
        }
        /* ------------ */

        /* Добавление нового коммента в массив элемента blogs */
        function addCommenToBlogAndItemArray() {

            let formBlog = document.querySelector('.form__blog');

            // if (!validate(formBlog)) {
            //     return;
            // }

            let name = formBlog.querySelector('.form-blog__input--name');
            let email = formBlog.querySelector('.form-blog__input--email');
            let website = formBlog.querySelector('.form-blog__input--website');
            let text = formBlog.querySelector('.form-blog__textarea');

            if (text.value.trim() === '') {
                return;
            }

            let currentPostId = document.querySelector('.blog').getAttribute('data-id');

            let blogsItem = searchBlogsItem(currentPostId);

            let newComment = {};

            newComment.author = name.value;
            newComment.email = email.value;
            newComment.website = website.value;
            newComment.text = text.value;

            blogsItem.commentsArray.push(newComment);


            let commentToPost = document.createElement('div');
            commentToPost.classList.add('comment');

            let author = document.createElement('span');
            author.classList.add('posted-by');
            author.textContent = name.value;

            let commentText = document.createElement('p');
            commentText.classList.add('comment__text');
            commentText.textContent = text.value;

            commentToPost.insertAdjacentElement('beforeend', author);
            commentToPost.insertAdjacentElement('beforeend', commentText);

            document.querySelector('.comments').insertAdjacentElement('beforeend', commentToPost);


            text.value = '';


            let serial = JSON.stringify(blogsItem.commentsArray);
            localStorage.setItem(currentPostId, serial);
            let parseJson = JSON.parse(localStorage.getItem(currentPostId));
            console.log(parseJson);
        }
