        /* Добавляет выбранный пост в blog */
        function addPostToBlog(postId) {

            let blogsItem = searchBlogsItem(postId);

            let blog = document.querySelector('.blog');

            blog.querySelector('.blog__img').style.backgroundImage = 'url(' + blogsItem.image + ')';
            blog.querySelector('.date__day').textContent = blogsItem.date;
            blog.querySelector('.blog__blog-title').textContent = blogsItem.title;
            blog.querySelector('.author').textContent = blogsItem.author;
            blog.querySelector('.blog__text').textContent = blogsItem.text;
            blog.setAttribute('data-id', postId);

            let comments = document.querySelector('.comments');
            comments.innerHTML = '';

            addCommentsFromPost(postId);

            /* Добавляет комменты из массива blogs-елемента */
            function addCommentsFromPost(postId) {

                // let postElem = searchBlogsItem(postId);
                // let postElemComments = postElem.commentsArray;

                let postElemComments = JSON.parse(localStorage.getItem(postId));

                for (let i = 0; i < postElemComments.length; i++) {
                    let newComment = document.createElement('div');
                    newComment.classList.add('comment');

                    let author = document.createElement('span');
                    author.classList.add('posted-by');
                    author.textContent = postElemComments[i].author;

                    let commentText = document.createElement('p');
                    commentText.classList.add('comment__text');
                    commentText.textContent = postElemComments[i].text;

                    newComment.insertAdjacentElement('beforeend', author);
                    newComment.insertAdjacentElement('beforeend', commentText);

                    document.querySelector('.comments').insertAdjacentElement('beforeend', newComment);
                }
            }
            /* ------------ */

        }
        /* ------------ */


